INSERT INTO `article` (`id`, `name`, `head_img`, `descript`) VALUES
(1, 'ประวัติ ลีโอนาร์โด ดิแคพรีโอ (Leonardo DiCaprio)', 'pic1.jpg', 'ลีโอนาร์โด ดิแคพรีโอ (Leonardo DiCaprio) เกิด 11 พฤศจิกายน พ.ศ. 2517 นักแสดงชาวอเมริกันชื่อดังผู้ซึ่งกวาดรางวัลมาแล้วทั้งรางวัลลูกโลกทองคำ , รางวัลแบฟตา และรางวัลออสการ์ ลีโอนาร์โด ดิแคพรีโอ เริ่มต้นงานการแสดงภาพยนตร์แรกกับภาพยนร์เรื่อง Critters 3 แต่ผลงานการแจ้งเกิดในตัวเขาเป็นที่รู้จักมาจากการแสดงในภาพยนตร์เรื่อง This Boy’s Life ที่แสดงร่วมกับ รอเบิร์ต เดอ นิโร (Robert De Niro) และ เอลเลน บาร์กิน (Ellen Barkin)\r\n\r\nต่อมาในปีค.ศ. 1993 ลีโอนาร์โด ดิแคพรีโอ ได้ร่วมแสดงกับ จอห์นนี เดปป์ ในภาพยนตร์เรื่อง What’s Eating Gilbert Grape การแสดงในครั้งนั้นทำให้ ลีโอนาร์โด ดิแคพรีโอ ได้รับการเสนอชื่อให้เข้าชิงรางวัลออสการ์และลูกโลกทองคำเป็นครั้งแรกทองคำเป็นครั้งแรก และได้รับรางวัล National Board of Review Award สาขา Best Supporting Actor และรางวัล Los Angeles Film Critics Association’s New Generation Award ในปีต่อมา ลีโอนาร์โด ดิแคพรีโอ ได้แสดงภาพยนตร์เรื่อง Romeo + Juliet ที่ดัดแปลงจากวรรณกรรมชื่อดังของ วิลเลียม เชกสเปียร์ (William Shakespeare) ส่งผลให้เขาได้รับรางวัล Best Actor Award ที่ Berlin International Film Festival\r\n\r\nในปีค.ศ. 1997 ลีโอนาร์โด ดิแคพรีโอ ได้แสดงภาพยนตร์เรื่อง Titanic ทำให้เขาได้รับการเสนอชื่อเข้าชิงรางวัลลูกโลกทองคำ โดบภาพยนตร์เรื่อง Titanic ได้คว้ารางวัลจากเวทีออสการ์ถึง 11 รางวัล โดยปัจจุบันภาพยนตร์เรื่องอยู่ในอันดับที่ 3 ภาพยนตร์ที่ทำรายได้สูงสุดตลอดกาล หลังจากที่ ลีโอนาร์โด ดิแคพรีโอ ได้ถูกเสนอชื่อเข้าชิงรางวัลออสการ์อยู่หลายครั้งแต่ไม่ถูกรับเลือกให้เป็นผู้ชนะเลยซักครั้ง ในที่สุดในปีค.ศ. 2015 ลีโอนาร์โด ดิแคพรีโอ ก็สามารถความรางวัลออสการ์ สาขานักแสดงนำชายยอดเยี่ยม ตัวแรกในชีวิตมาครองได้จากการแสดงในภาพยนตร์เรื่อง The Revenant'),
(2, 'ประวัติ ชารอน สโตน (Sharon Stone)', 'pic2.png', 'Sharon Vonne Stone ด้านการศึกษา ชารอน สโตน ศึกษาที่โรงเรียนมัธยมปลายที่เซเกอร์ทาวน์ เพนน์ซิลวาเนีย และเข้าศึกษาต่อที่มหาวิทยาลัยเอ็ดดิโบโร ของเพนน์ซิลวาเนีย ซึ่งหลังจากนั้นเธอก็ได้ชนะการประกวดมิสเพนน์ซิลวาเนีย บิวตี้ คอนเทสต์ และก้าวเข้าสู่วงการนางแบบ ก่อนที่จะมีผลงานโทรทัศน์และโฆษณาตามมา ในปี 1980 เธอก็ได้แสดงใน Woody Allen’s Stardust Memories ชารอนเป็นลูกคนที่สอง จากทั้งหมด 4 คน เมื่อตอนที่เธออายุได้ 15 ปี ชารอนเรียนที่โรงเรียนมัธยมปลายที่เซเกอร์ทาวน์ เพนน์ซิลวาเนีย และเธอก็ได้เข้าเรียนต่อที่มหาวิทยาลัยเอ็ดดิโบโร ของเพนน์ซิลวาเนีย และเมื่อเธอจบการศึกษาในสาขาการวรรณศิลป์ เธอเป็นสาวที่ชาญฉลาดด้วยไอคิว 154 และยังเป็นหนอนหนังสือตัวยง ครั้งนึงเธอเคยบอกว่างานที่เหมาะสมกับเธอ และสมองของเธอคือการเป็นนักกฏหมาย เป็นนักแสดงชาวอเมริกัน โปรดิวเซอร์ และอดีตนางแบบ ซึ่งเธอเป็นที่รู้จักในผลงานภาพยนตร์เรื่อง Basic Instinct และในปี ค.ศ. 1958 เธอได้รับการเสนอชื่อเข้าชิงรางวัลออสการ์สาขานักแสดงนำหญิงยอดเยี่ยมจากเรื่อง Casino และได้รับรางวัลจากรางวัลลูกโลกทองคำ และรางวัลเอมมี'),
(3, 'ประวัติ แดเนียล จาคอบ แรดคลิฟฟ์ (Daniel Jacob Radcliffe)', 'pic3.jpg', 'แดเนียล เรดคลิฟฟ์ (Daniel Radcliffe) มีชื่อจริงเต็มๆว่า แดเนียล จาคอบ แรดคลิฟฟ์ (Daniel Jacob Radcliffe) เกิดวันที่ 23 กรกฎาคม ค.ศ. 1989 นักแสดงชาวอังกฤษที่มีชื่อเสียงจากการรับบทเป็น แฮร์รี่ พอตเตอร์ จากภาพยนตร์ชุด Harry Potter ภาพยนตร์ที่สร้างจากนิยายชื่อดังของ เจ. เค. โรว์ลิง (J. K. Rowling) แดเนียล เรดคลิฟฟ์ เริ่มมีผลงานการแสดงครั้งแรกตอนอายุ 10 ขวบ ในทีวีมินิซีรีส์เรื่อง David Copperfield โดยตัวเขาได้รับบทเป็น เดวิด คอปเปอร์ฟิลด์ ยอดนักมายากลในวัยเด็ก พออายุได้ 11 ขวบ เขาได้รับเลือกให้แสดงเป็น แฮร์รี่ พอตเตอร์ และแสดงในบทบาทนี้มาเป็นเวลายาวนานเป็นเวลา 10 ปี\r\n\r\nในปีค.ศ. 2007 แดเนียล เรดคลิฟฟ์ ได้แสดงในละครเวทีครั้งแรกเรื่อง Equus และเขาได้รับรางวัล Best Leading Actor จาก Annual Theatre Fan Choice Awards ซึ่งจัดขึ้นโดย Broadway World นอกจากนั้นเขายังได้รับการเสนอชื่อเข้าชิงรางวัล Drama League และ Drama Desk อีกด้วย\r\n\r\nนอกจากงานด้านการแสดง แดเนียล เรดคลิฟฟ์ ยังอุทิศตนเพื่องานการกุศลมากมาย เช่น ที่พักฟื้นสำหรับผู้ป่วยเด็กระยะสุดท้ายเดเมลซา และเดอะเทรเวอร์โปรเจกต์ โครงการเพื่อป้องกันการฆ่าตัวตายในวัยรุ่นที่มีความหลากหลายทางเพศ ทำให้เขาได้รับรางวัล HERO Awards ในปี ค.ศ. 2011');